using System.Collections;
using System.Collections.Generic;
using TMPro;
using UnityEngine;

public class TextFromData : MonoBehaviour
{
    public string type = "";
    private TextMeshProUGUI jessie;
    public bool UseFixedUpdate = false;
    // Start is called before the first frame update
    void OnEnable()
    {
        jessie= GetComponent<TextMeshProUGUI>();
    }

    // Update is called once per frame
    void Update()
    {
        if (!UseFixedUpdate)
        {
            j();
        }
    }
    void FixedUpdate()
    {
        if (UseFixedUpdate)
        {
            j();
        }
    }
    public void j()
    {

        string a = "";
        switch (type)
        {
            case "TestNumber":
                a = RandomFunctions.Instance.NumToRead("");
                break;
            case "TestNumber2":
                a = RandomFunctions.Instance.NumToRead("-.169");
                break;
            case "TestNumber3":
                a = RandomFunctions.Instance.NumToRead("-.55", 2);
                break;
            case "TestNumber4":
                a = RandomFunctions.Instance.NumToRead("-10000.69", 0) + "<br>" + RandomFunctions.Instance.NumToRead("-10000.69", 1) + "<br>" + RandomFunctions.Instance.NumToRead("-10000.69", 2) + "<br>";
                break;
            case "CountMax":
                a = $"{Tags.refs["RightTrans"].transform.childCount}/{Gamer.CurrentFloor}";
                break;
            case "TimeUntilQuest":
                var ff = (RandomFunctions.Instance.GetUnixTime());
                var ff2 = (Gamer.Instance.TimeOfQuest+1) * 60 * 60 * 2;
                a = $"Time Until Refresh: {Converter.TimeToRead((long)(ff2-ff))}";
                break;
            case "Coins":
                a = PlayerController.Instance.Coins.ToString();
                break;
        }

        jessie.text = a;
    }
}
